(function(){function init(){if($('help_link')){$('help_link').observe('click',function(event){Event.stop(event);MyBB.popupWindow(this.href,'asbHelp',840,520);});}
if($('help_link_icon')){$('help_link_icon').observe('click',function(event){Event.stop(event);MyBB.popupWindow(this.up('a').href,'asbHelp',840,520);});}}
Event.observe(window,'load',init);})();