(function(){function init(){if($('#asb_hide_column_left')){$('#asb_hide_column_left').click(toggle);}
if($('#asb_hide_column_right')){$('#asb_hide_column_right').click(toggle);}}
function toggle(event){event.preventDefault();var position='left';if(this.id=='asb_hide_column_right'){position='right';}
var cookieName='asb_hide_'+position,column=$('#asb_'+position+'_column_id'),closeIcon=$('#asb_'+position+'_close'),openIcon=$('#asb_'+position+'_open');var hide=Cookie.get(cookieName);if(hide==0||typeof hide=='undefined'){column.hide();closeIcon.hide();openIcon.show();Cookie.set(cookieName,1);}else{column.show();closeIcon.show();openIcon.hide();Cookie.unset(cookieName);}}
$(init);})();